import {
  LOGIN_START,
  LOGIN_SUCCESS,
  LOGIN_FAILURE,
  LOGOUT,
  UPDATE_START,
  UPDATE_SUCCESS,
  UPDATE_FAILURE,
} from "./ActionType";

export const Reducer = (state, action) => {
  switch (action.type) {
    case LOGIN_START:
      return {
        user: null,
        isFetching: true,
        error: false,
      };
    case LOGIN_SUCCESS:
      return {
        user: action.payload,
        isFetching: false,
        error: false,
      };
    case LOGIN_FAILURE:
      return {
        user: null,
        isFetching: false,
        error: true,
      };
    case UPDATE_START:
      return {
        user: null,
        isFetching: true,
        error: false,
      };
    case UPDATE_SUCCESS:
      return {
        user: action.payload,
        isFetching: false,
        error: false,
      };
    case UPDATE_FAILURE:
      return {
        user: null,
        isFetching: false,
        error: true,
      };
    case LOGOUT:
      return {
        user: null,
        isFetching: false,
        error: false,
      };

    default:
      return state;
  }
};
