import Axios from "axios";
import React, { useState } from "react";
import "./register.css";

function Register() {
  const [user, setUser] = useState({});

  const handleSummit = async () => {
    try {
      const res = fetch("/auth/register", {
        method: "post",
        headers: { accepts: "application/json" },
        body: JSON.stringify(user),
      });
    } catch (error) {
      console.log(error);
    }
  };
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser((val) => ({ ...val, [name]: value }));
  };

  return (
    <div className="register">
      <h3 className="registertitle">Register</h3>
      <form className="registerform" onSubmit={handleSummit}>
        <label className="registerlabel" type="text">
          Username
        </label>
        <input
          className="registerinput"
          placeholder="enter username"
          name="name"
          onChange={handleChange}
          value={user.name}
        />
        <label className="registerlabel" type="text">
          Email
        </label>
        <input
          className="registerinput"
          placeholder="enter email"
          name="email"
          onChange={handleChange}
          value={user.email}
        />
        <label className="registerlabel">password</label>
        <input
          type="password"
          className="registerinput"
          placeholder="enter password"
          name="password"
          onChange={handleChange}
          value={user.password}
        />
        <button className="registerBtn">Register</button>
      </form>
      <div className="registerLogin">
        <h5 className="registerLogintitle">
          Already have an account, click here
        </h5>
        <button className="registerLoginbtn"> Register</button>
      </div>
    </div>
  );
}

export default Register;
