import React from "react";
import { NavLink } from "react-router-dom";
//import styled from "styled-components";

function Posts(props) {
  const Pf = "http://localhost:8000/images/";

  const postData = props.posts.map((post, i) => (
    <div className="col-md-3 col-sm-12" key={post.id}>
      <div className="card" style={{ width: "18rem" }}>
        {post.photo && (
          <img
            classNameName="card-img-top"
            height="250px"
            src={Pf + post.photo}
            alt="post cover"
          />
        )}
        <div className="card-body">
          <h5 className="card-title">{post.title}</h5>
          <p className="card-text"> {post.description.substring(0, 6)}</p>
          <NavLink to={`/post/${post._id}`} className="btn btn-primary">
            Read More
          </NavLink>
        </div>
      </div>
    </div>
  ));
  return (
    <div className="container">
      <div className="row">{postData}</div>
    </div>
  );
}

export default Posts;
