import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { NavLink, useParams } from "react-router-dom";
import "./singlepost.css";
import { Context } from "../context/Context";

function SinglePost() {
  const { id } = useParams();

  //const path = useLocation().pathname.split("/")[2];

  const [post, setPost] = useState([]);
  const { user } = useContext(Context);

  useEffect(() => {
    const getpost = async () => {
      try {
        const res = await axios.get(`/post/${id}`);
        setPost(res.data);
      } catch (error) {
        console.log(error);
      }
    };
    getpost();
  }, [id]);
  const Pf = "http://localhost:8000/images/";

  const handleUpdate = () => {};

  const handleDelete = async () => {
    try {
      await axios.delete(`/post/${id}`, { username: user.username });
      window.location.replace("/");
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="singlepost">
      <div className="wrapper">
        {post.photo && (
          <img
            src={Pf + post.photo}
            alt="post cover"
            className="singlepostimage"
          />
        )}

        <h1 className="singlepostitle">
          {post.title}
          {post.username === user?.username && (
            <div className="edit">
              <i
                className="fa-solid fa-pen-to-square editIcon"
                onClick={handleUpdate}
              ></i>
              <i
                className="fa-solid fa-trash-can editIcon "
                onClick={handleDelete}
              ></i>
            </div>
          )}
        </h1>
        <div className="singlepostinfo">
          <span>
            <NavLink to={`/?user=${post.username}`}>
              {" "}
              Authur: <b>{post.username}</b>
            </NavLink>
          </span>
          <span>{new Date(post.createdAt).toDateString()}</span>
        </div>
        <p className="singlepostdesc">{post.description}</p>
      </div>
    </div>
  );
}

export default SinglePost;
