import React, { useEffect, useState } from "react";
import axios from "axios";
import { NavLink, useParams } from "react-router-dom";
import "./singlepost.css";

function SinglePost() {
  const { id } = useParams();
  //const path = useLocation().pathname.split("/")[2];

  const [post, setPost] = useState([]);

  useEffect(() => {
    const getpost = async () => {
      try {
        const res = await axios.get(`/post/${id}`);
        setPost(res.data);
      } catch (error) {
        console.log(error);
      }
    };
    getpost();
  }, [id]);

  return (
    <div className="singlepost">
      <div className="wrapper">
        {post.photo && (
          <img src={post.photo} alt="post cover" className="singlepostimage" />
        )}

        <h1 className="singlepostitle">
          {post.title}
          <div className="edit">
            <i className="fa-solid fa-pen-to-square editIcon"></i>
            <i className="fa-solid fa-trash-can editIcon"></i>
          </div>
        </h1>
        <div className="singlepostinfo">
          <span>
            <NavLink to={`/?user=${post.username}`}>
              {" "}
              Authur: <b>{post.username}</b>
            </NavLink>
          </span>
          <span>{new Date(post.createdAt).toDateString()}</span>
        </div>
        <p className="singlepostdesc">{post.description}</p>
      </div>
    </div>
  );
}

export default SinglePost;
