import React, { useState, useContext } from "react";
import axios from "axios";
import "./write.css";
import { Context } from "../context/Context";

function Write() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [photo, setPhoto] = useState(null);
  const { user } = useContext(Context);

  // const handleChange = (e) => {
  //   const { name, value } = e.target;
  //   setData({ ...data, [name]: value });
  // };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newPost = { title, description, username: user.username };
    if (photo) {
      const data = FormData();
      const filename = Date.now.apply() + photo.name;
      data.append("name", filename);
      data.append("file", photo);
    }
    axios.post("/post");
  };

  return (
    <div className="write">
      <img src="/images/gallery5.jpeg" alt="writeimg" className="writeImg" />
      <form className="writeform" onSubmit={handleSubmit}>
        <div className="writeformgroup">
          <label htmlFor="fileinput">
            <i className="fa-solid fa-plus writeIcon"></i>
          </label>
          <input
            type="file"
            name="photo"
            id="fileinput"
            style={{ display: "none" }}
          />
          <label htmlFor="textinput">Title</label>
          <input
            type="text"
            name="title"
            id="textinput"
            className="textinput"
          />
        </div>
        <div className="writeformgroup">
          <textarea
            type="text"
            className="textinput writetext"
            placeholder="write your story"
            name="description"
          ></textarea>
        </div>
        <button className="writeBtn" type="submit">
          Publish
        </button>
      </form>
    </div>
  );
}

export default Write;
