import React, { useState } from "react";
import "./write.css";

function Write() {
  const [data, setData] = useState({ title: "", decription: "", file: null });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(data);
  };

  return (
    <div className="write">
      <img src="/images/gallery5.jpeg" alt="writeimg" className="writeImg" />
      <form className="writeform" onSubmit={handleSubmit}>
        <div className="writeformgroup">
          <label htmlFor="fileinput">
            <i className="fa-solid fa-plus writeIcon"></i>
          </label>
          <input
            type="file"
            name="file"
            id="fileinput"
            style={{ display: "none" }}
            onChange={handleChange}
          />
          <label htmlFor="textinput">Title</label>
          <input
            type="text"
            name="title"
            id="textinput"
            className="textinput"
            onChange={handleChange}
          />
        </div>
        <div className="writeformgroup">
          <textarea
            type="text"
            className="textinput writetext"
            placeholder="write your story"
            name="description"
            onChange={handleChange}
          ></textarea>
        </div>
        <button className="writeBtn" type="submit">
          Publish
        </button>
      </form>
    </div>
  );
}

export default Write;
