import React from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";

function Posts(props) {
  const Pf = "http://localhost:8000/images/";

  //style section
  const Wrapper = styled.div`
    display: flex;
  `;

  const postData = props.posts.map((post, i) => (
    <>
      {/* <Wrapper>
        <div className="post">
          {post.photo && <img src={Pf + post.photo} alt="post cover" />}

          <div className="postInfo">
            <div className="postCart">
              <span>from {post.username}</span>
            </div>
            <span className="postTitle">{post.title}</span>
            <hr />
            <span className="postDate">
              {new Date(post.createdAt).toDateString()}
            </span>
          </div>
          <p className="postDesc">{post.description}</p>
          <NavLink
            className="btn btn-dark"
            style={{ color: "white", fontWeight: "600" }}
            to={`/post/${post._id}`}
          >
            Read more
          </NavLink>
        </div>
      </Wrapper> */}
      <div className="col-md-3 mb-4">
        <div className="card h-100 text-center p-4" key={post.id}>
          {post.photo && (
            <img
              className="card-img-top"
              height="250px"
              src={Pf + post.photo}
              alt="post cover"
            />
          )}
          {/* <img
            className="card-img-top"
            height="250px"
            src={Pf + post.photo}
            alt=""
          /> */}
          <div className="card-body">
            <h5 className="card-title mb-0">{post.title.substring(0, 12)}</h5>
            <p className="card-text lead fw-bold ">${post.description}</p>
            <NavLink to={`/post/${post._id}`} className="btn btn-outline-dark ">
              Read more
            </NavLink>
          </div>
        </div>
      </div>
    </>
  ));
  return <>{postData}</>;
}

export default Posts;
