import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { tablet } from "../Responsive";

//css section
const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  background-color: rgb(78, 77, 77);
  color: rgb(212, 201, 201);
  padding: 20px;

  .item1 {
    display: flex;
    margin-right: 10px;
  }

  .icon {
    height: 30px;
    width: 30px;
    border-radius: 50%;
    margin-right: 20px;
  }
  .footer-container .foot-item div span {
    font-size: 0.5rem;
    margin-right: 20px;
    margin-left: 5px;
  }
  .footer-container .foot-item {
    margin: 0;
    padding: 0;
  }
  ul {
    list-style-type: none;
    margin: 0;
    padding: 0;
    margin-top: 20px;
  }
  li {
    margin-bottom: 15px;
  }
  a {
    text-decoration: none;
    color: rgb(212, 201, 201);
  }
  a:hover {
    font-weight: bold;
    color: #fff;
  }
`;

function Footer() {
  return (
    <Wrapper>
      <div className="item1">
        <img src="brand.png" alt="logo" />
        <h3 className="name">All Voice Blog</h3>
      </div>
      <ul>
        <li>
          <Link className="icon" href="index.html">
            Home
          </Link>
        </li>
        <li>
          <Link className="icon" href="about.html">
            About
          </Link>
        </li>
        <li>
          <Link className="icon" href="contact.html">
            Contact
          </Link>
        </li>
      </ul>

      <div className="item2">
        <h4>Follow All Voice</h4>
        <div className="footimg-container"></div>
      </div>
      <p>
        <em>Copyright at Idoko</em>
      </p>
    </Wrapper>
  );
}

export default Footer;
