import React from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";

function Posts(props) {
  const Pf = "http://localhost:8000/images/";

  //style section
  const Wrapper = styled.div`
    display: flex;
  `;

  const postData = props.posts.map((post, i) => (
    <>
      {/* <Wrapper>
        <div className="post">
          {post.photo && <img src={Pf + post.photo} alt="post cover" />}

          <div className="postInfo">
            <div className="postCart">
              <span>from {post.username}</span>
            </div>
            <span className="postTitle">{post.title}</span>
            <hr />
            <span className="postDate">
              {new Date(post.createdAt).toDateString()}
            </span>
          </div>
          <p className="postDesc">{post.description}</p>
          <NavLink
            className="btn btn-dark"
            style={{ color: "white", fontWeight: "600" }}
            to={`/post/${post._id}`}
          >
            Read more
          </NavLink>
        </div>
      </Wrapper> */}
      <div class="card" style={{ width: "18rem" }} key={post.id}>
        {post.photo && (
          <img
            className="card-img-top"
            height="250px"
            src={Pf + post.photo}
            alt="post cover"
          />
        )}
        <div class="card-body">
          <h5 class="card-title">{post.title}</h5>
          <p class="card-text"> {post.description.substring(0, 6)}</p>
          <a href={`/post/${post._id}`} class="btn btn-primary">
            Read More
          </a>
        </div>
      </div>
    </>
  ));
  return (
    <div className="container">
      <div>{postData}</div>
    </div>
  );
}

export default Posts;
