import React, { useState } from "react";
import "./write.css";

function Write() {
  const [data, setData] = useState({ title: "", decription: "", file: null });
  return (
    <div className="write">
      <img src="/images/gallery5.jpeg" alt="writeimg" className="writeImg" />
      <form className="writeform">
        <div className="writeformgroup">
          <label htmlFor="fileinput">
            <i className="fa-solid fa-plus writeIcon"></i>
          </label>
          <input type="file" id="fileinput" style={{ display: "none" }} />
          <label for="textinput">Title</label>
          <input type="text" id="textinput" className="textinput" />
        </div>
        <div className="writeformgroup">
          <textarea
            type="text"
            className="textinput writetext"
            placeholder="write your story"
          ></textarea>
        </div>
        <button className="writeBtn">Publish</button>
      </form>
    </div>
  );
}

export default Write;
